            </>
          ) : (
            <Button
              variant="outline"
              size="sm"
              onClick={() => window.location.href = "/api/login"}
              className="border-white text-white hover:bg-white hover:text-black"
              data-testid="button-login"
            >
              <User className="w-4 h-4 mr-2" />
