import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import Navbar from "@/components/Navbar";
import { Card } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import type { Order, Artwork } from "@shared/schema";
import { Package, Clock, CheckCircle, XCircle, Truck } from "lucide-react";

const ordersAPI = {
  getUserOrders: async (whatsapp: string): Promise<Order[]> => {
    const response = await fetch(`/api/orders/user/${encodeURIComponent(whatsapp)}`);
    if (!response.ok) throw new Error("Failed to fetch orders");
    return response.json();
  },
  updateOrder: async (orderId: string, data: Partial<Order>) => {
    const response = await apiRequest("PATCH", `/api/orders/${orderId}`, data);
    return response.json();
  }
};

const artworksAPI = {
  getById: async (id: string): Promise<Artwork> => {
    const response = await fetch(`/api/artworks?id=${id}`);
    if (!response.ok) throw new Error("Failed to fetch artwork");
    const artworks = await response.json();
    return artworks.find((a: Artwork) => a.id === id);
  }
};

function OrderCard({ order, language, artworks }: { 
  order: Order; 
  language: "en" | "ar";
  artworks: Record<string, Artwork>;
}) {
  const [paymentMethod, setPaymentMethod] = useState<"vodafone_cash" | "instapay">(
    order.paymentMethod || "vodafone_cash"
  );
  const [paymentProof, setPaymentProof] = useState(order.paymentProof || "");
  const { toast } = useToast();

  const updateOrderMutation = useMutation({
    mutationFn: ({ orderId, data }: { orderId: string; data: Partial<Order> }) =>
      ordersAPI.updateOrder(orderId, data),
    onSuccess: () => {
      toast({
        title: language === "en" ? "Updated!" : "ÿ™ŸÖ ÿßŸÑÿ™ÿ≠ÿØŸäÿ´!",
        description: language === "en" 
          ? "Payment information updated successfully" 
          : "ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑÿØŸÅÿπ ÿ®ŸÜÿ¨ÿßÿ≠"
      });
      queryClient.invalidateQueries({ queryKey: ["/api/orders"] });
    }
  });

  const artwork = artworks[order.artworkId];
  if (!artwork) return null;

  const statusConfig = {
    pending: {
      icon: Clock,
      label: language === "en" ? "Pending Payment" : "ŸÅŸä ÿßŸÜÿ™ÿ∏ÿßÿ± ÿßŸÑÿØŸÅÿπ",
      color: "bg-yellow-500",
      textColor: "text-yellow-700 dark:text-yellow-400"
    },
    confirmed: {
      icon: CheckCircle,
      label: language === "en" ? "Confirmed" : "ŸÖÿ§ŸÉÿØ",
      color: "bg-green-500",
      textColor: "text-green-700 dark:text-green-400"
    },
    shipped: {
      icon: Truck,
      label: language === "en" ? "Shipped" : "ÿ™ŸÖ ÿßŸÑÿ¥ÿ≠ŸÜ",
      color: "bg-blue-500",
      textColor: "text-blue-700 dark:text-blue-400"
    },
    cancelled: {
      icon: XCircle,
      label: language === "en" ? "Cancelled" : "ŸÖŸÑÿ∫Ÿä",
      color: "bg-red-500",
      textColor: "text-red-700 dark:text-red-400"
    },
    refunded: {
      icon: Package,
      label: language === "en" ? "Refunded" : "ÿ™ŸÖ ÿßŸÑÿßÿ≥ÿ™ÿ±ÿØÿßÿØ",
      color: "bg-gray-500",
      textColor: "text-gray-700 dark:text-gray-400"
    }
  };

  const status = statusConfig[order.status];
  const StatusIcon = status.icon;

  const handleSubmitPayment = () => {
    updateOrderMutation.mutate({
      orderId: order.id,
      data: {
        paymentMethod,
        paymentProof
      }
    });
  };

  return (
    <Card className="p-6" data-testid={`card-order-${order.id}`}>
      <div className="flex gap-4">
        <img
          src={artwork.images[0]}
          alt={artwork.title}
          className="w-24 h-24 object-cover rounded-md"
          data-testid="img-artwork"
        />

        <div className="flex-1">
          <div className="flex items-start justify-between mb-2">
            <div>
              <h3 className="font-serif text-xl font-bold" data-testid="text-artwork-title">
                {artwork.title}
              </h3>
              <p className="text-sm text-muted-foreground">
                {language === "en" ? "Size:" : "ÿßŸÑŸÖŸÇÿßÿ≥:"} {order.size}
              </p>
            </div>
            <Badge className={status.textColor} data-testid="badge-status">
              <StatusIcon className="w-4 h-4 mr-1" />
              {status.label}
            </Badge>
          </div>

          <div className="grid grid-cols-2 gap-4 mt-4 text-sm">
            <div>
              <span className="text-muted-foreground">{language === "en" ? "Price:" : "ÿßŸÑÿ≥ÿπÿ±:"}</span>
              <span className="ml-2 font-semibold gold-metallic" data-testid="text-price">
                {(order.priceCents / 100).toLocaleString()} {language === "en" ? "EGP" : "ÿ¨ŸÜŸäŸá"}
              </span>
            </div>
            <div>
              <span className="text-muted-foreground">{language === "en" ? "Order Date:" : "ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ∑ŸÑÿ®:"}</span>
              <span className="ml-2 font-semibold">
                {new Date(order.createdAt).toLocaleDateString()}
              </span>
            </div>
          </div>

          {order.status === "pending" && (
            <div className="mt-4 p-4 bg-muted rounded-md space-y-3">
              <h4 className="font-semibold">
                {language === "en" ? "Upload Payment Proof" : "ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ´ÿ®ÿßÿ™ ÿßŸÑÿØŸÅÿπ"}
              </h4>

              <div className="space-y-2">
                <Label>{language === "en" ? "Payment Method" : "ÿ∑ÿ±ŸäŸÇÿ© ÿßŸÑÿØŸÅÿπ"}</Label>
                <Select value={paymentMethod} onValueChange={(v: any) => setPaymentMethod(v)}>
                  <SelectTrigger data-testid="select-payment-method">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="vodafone_cash">Vodafone Cash</SelectItem>
                    <SelectItem value="instapay">InstaPay</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
