export default function Profile() {
  const [language, setLanguage] = useState<"en" | "ar">("en");
  const [whatsapp, setWhatsapp] = useState("");
  const [submittedWhatsapp, setSubmittedWhatsapp] = useState("");

  const { data: orders = [], isLoading } = useQuery({
    queryKey: ["/api/orders", "user", submittedWhatsapp],
    queryFn: () => ordersAPI.getUserOrders(submittedWhatsapp),
    enabled: !!submittedWhatsapp
  });

  const { data: artworks = {} } = useQuery({
    queryKey: ["/api/artworks", "lookup"],
    queryFn: async () => {
      const response = await fetch("/api/artworks");
      const allArtworks: Artwork[] = await response.json();
      return allArtworks.reduce((acc, artwork) => {
        acc[artwork.id] = artwork;
        return acc;
      }, {} as Record<string, Artwork>);
    }
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setSubmittedWhatsapp(whatsapp);
  };

  return (
    <div className="min-h-screen bg-background">
      <Navbar currentLang={language} onLanguageChange={setLanguage} />

      <div className="pt-24 px-4 pb-20">
        <div className="max-w-4xl mx-auto">
          <h1 className="font-serif text-4xl md:text-5xl font-bold mb-8 text-center" data-testid="text-page-title">
            {language === "en" ? "My Orders" : "ÿ∑ŸÑÿ®ÿßÿ™Ÿä"}
          </h1>

          {!submittedWhatsapp ? (
            <Card className="p-8 max-w-md mx-auto">
              <h2 className="font-serif text-2xl font-bold mb-4 text-center">
                {language === "en" ? "View Your Orders" : "ÿπÿ±ÿ∂ ÿ∑ŸÑÿ®ÿßÿ™ŸÉ"}
              </h2>
              <p className="text-muted-foreground mb-6 text-center">
                {language === "en" 
                  ? "Enter your WhatsApp number to view your order history" 
                  : "ÿ£ÿØÿÆŸÑ ÿ±ŸÇŸÖ ÿßŸÑŸàÿßÿ™ÿ≥ÿßÿ® ÿßŸÑÿÆÿßÿµ ÿ®ŸÉ ŸÑÿπÿ±ÿ∂ ÿ≥ÿ¨ŸÑ ÿ∑ŸÑÿ®ÿßÿ™ŸÉ"}
              </p>
              <form onSubmit={handleSubmit} className="space-y-4">
                <Input
                  type="tel"
                  placeholder={language === "en" ? "WhatsApp Number" : "ÿ±ŸÇŸÖ ÿßŸÑŸàÿßÿ™ÿ≥ÿßÿ®"}
                  value={whatsapp}
                  onChange={(e) => setWhatsapp(e.target.value)}
                  required
                  data-testid="input-whatsapp"
                />
                <Button type="submit" className="w-full" data-testid="button-view-orders">
                  {language === "en" ? "View Orders" : "ÿπÿ±ÿ∂ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™"}
                </Button>
              </form>
            </Card>
          ) : (
            <>
              <div className="flex justify-between items-center mb-6">
                <p className="text-muted-foreground">
                  {language === "en" ? "Showing orders for:" : "ÿπÿ±ÿ∂ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™ ŸÑŸÄ:"} {submittedWhatsapp}
                </p>
                <Button
                  variant="outline"
                  onClick={() => {
                    setSubmittedWhatsapp("");
                    setWhatsapp("");
                  }}
                  data-testid="button-change-number"
                >
                  {language === "en" ? "Change Number" : "ÿ™ÿ∫ŸäŸäÿ± ÿßŸÑÿ±ŸÇŸÖ"}
                </Button>
              </div>

              {isLoading ? (
                <div className="text-center py-20">
                  <p className="text-xl text-muted-foreground">
                    {language === "en" ? "Loading orders..." : "ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™..."}
                  </p>
                </div>
              ) : orders.length === 0 ? (
                <div className="text-center py-20">
                  <Package className="w-16 h-16 mx-auto mb-4 text-muted-foreground" />
                  <p className="text-xl text-muted-foreground">
                    {language === "en" ? "No orders found" : "ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ∑ŸÑÿ®ÿßÿ™"}
                  </p>
                </div>
              ) : (
                <div className="space-y-4">
                  {orders.map(order => (
                    <OrderCard
                      key={order.id}
                      order={order}
                      language={language}
                      artworks={artworks}
                    />
                  ))}
                </div>
              )}
